def count_parentheses(s):
    return s.count('(') - s.count(')')

def count_Basementposition(s):
    floor=0
    for pos,ch in enumerate(s,start=1):
        if ch=='(':
            floor+=1
        else :
            floor-=1
        if floor==-1:
            return pos

    

